import React, { useState } from 'react';

const AddChatbotFields = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState({ id: 1 });

  const handleQuestionChange = (e, index) => {
    const { name, value } = e.target;
    const updatedQuestions = [...questions];
    updatedQuestions[index] = {
      ...updatedQuestions[index],
      [name]: value,
    };
    setQuestions(updatedQuestions);
  };

  const handleAddQuestion = () => {
    setQuestions([...questions, currentQuestion]);
    setCurrentQuestion({ id: currentQuestion.id + 1 });
  };

  const handleReorder = (dragIndex, hoverIndex) => {
    const dragQuestion = questions[dragIndex];
    const updatedQuestions = [...questions];
    updatedQuestions.splice(dragIndex, 1);
    updatedQuestions.splice(hoverIndex, 0, dragQuestion);
    setQuestions(updatedQuestions);
  };

  return (
    <div>
      <h2>Add Chatbot Fields</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Text</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {questions.map((question, index) => (
            <TableRow
              key={index}
              question={question}
              index={index}
              handleQuestionChange={handleQuestionChange}
              handleReorder={handleReorder}
            />
          ))}
        </tbody>
      </table>
      <div className="add-question-form">
        <div className="input-group">
          <input
            type="text"
            name="text"
            placeholder="Question Text"
            value={currentQuestion.text || ''}
            onChange={(e) => setCurrentQuestion({ ...currentQuestion, text: e.target.value })}
          />
        </div>
        <button onClick={handleAddQuestion}>Add Question</button>
      </div>
    </div>
  );
};

const TableRow = ({ question, index, handleQuestionChange, handleReorder }) => {
  const handleDragStart = (e) => {
    e.dataTransfer.setData('index', index.toString());
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dragIndex = parseInt(e.dataTransfer.getData('index'), 10);
    handleReorder(dragIndex, index);
  };

  return (
    <tr
      draggable
      onDragStart={handleDragStart}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
    >
      <td>{question.id}</td>
      <td>
        <input
          type="text"
          name="text"
          value={question.text || ''}
          onChange={(e) => handleQuestionChange(e, index)}
        />
      </td>
      <td>
        <button>Remove</button>
      </td>
    </tr>
  );
};

export default AddChatbotFields;
