import React, { useState, useRef } from 'react';
import './AddChatbotFields.css';
import { getNewTemplateId } from './values';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddChatbotFields = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState({
        id: 1,
        callValidations: [],
        options: [],
    });
    const [qid, setQid] = useState(2);
    const [draggedIndex, setDraggedIndex] = useState(null);
    const [editingIndex, setEditingIndex] = useState(-1);

    const callValidationInputRef = useRef(null);
    const optionValueInputRef = useRef(null);
    const optionLabelInputRef = useRef(null);

    const handleQuestionChange = (e) => {
        const { name, value } = e.target;
        setCurrentQuestion({
            ...currentQuestion,
            [name]: value,
        });
    };

    const addQuestion = () => {
        if (currentQuestion.text && currentQuestion.field && currentQuestion.mandatory) {
            setQuestions([...questions, currentQuestion]);
            setCurrentQuestion({
                id: qid,
                callValidations: [],
                options: [],
            });
            setQid(qid + 1);
        }
    };

    const addCallValidation = () => {
        if (currentQuestion.callValidations) {
            const updatedQuestion = { ...currentQuestion };
            updatedQuestion.callValidations.push('');
            setCurrentQuestion(updatedQuestion);
        }
    };

    const addOption = () => {
        if (currentQuestion.options) {
            const updatedQuestion = { ...currentQuestion };
            updatedQuestion.options.push({ value: '', label: '' });
            setCurrentQuestion(updatedQuestion);
        }
    };

    const handleValidationChange = (index, value) => {
        const updatedQuestion = { ...currentQuestion };
        updatedQuestion.callValidations[index] = value;
        setCurrentQuestion(updatedQuestion);
    };

    const handleOptionChange = (index, field, value) => {
        const updatedQuestion = { ...currentQuestion };
        updatedQuestion.options[index][field] = value;
        setCurrentQuestion(updatedQuestion);
    };

    const startEditing = (index) => {
        setEditingIndex(index);
    };

    const saveEditedQuestion = () => {
        if (editingIndex >= 0 && editingIndex < questions.length) {
            const updatedQuestions = [...questions];
            updatedQuestions[editingIndex] = currentQuestion;
            setQuestions(updatedQuestions);
            setEditingIndex(-1);
        }
    };

    const deleteQuestion = (index) => {
        if (index >= 0 && index < questions.length) {
            const updatedQuestions = [...questions];
            updatedQuestions.splice(index, 1);
            setQuestions(updatedQuestions);
        }
    };

    const handleDragStart = (index) => {
        setDraggedIndex(index);
    };

    const handleDragOver = (index) => {
        if (draggedIndex === null || index === draggedIndex) return;

        const updatedQuestions = [...questions];
        const [draggedItem] = updatedQuestions.splice(draggedIndex, 1);
        updatedQuestions.splice(index, 0, draggedItem);

        setQuestions(updatedQuestions);
        setDraggedIndex(index);
    };

    const handleDragEnd = () => {
        setDraggedIndex(null);
    };

    return (
        <div>
            <button onClick={goBack} className="back-button">
                &lt; Back
            </button>
            <div>
                <h2>Add Chatbot Fields</h2>
                <div className="add-question-form">
                    <div className="input-group">
                        <input
                            type="text"
                            name="text"
                            placeholder="Question Text"
                            value={currentQuestion.text || ''}
                            onChange={handleQuestionChange}
                        />
                        <input
                            type="text"
                            name="field"
                            placeholder="Field"
                            value={currentQuestion.field || ''}
                            onChange={handleQuestionChange}
                        />
                        <input
                            type="text"
                            name="fieldLength"
                            placeholder="Field Length"
                            value={currentQuestion.fieldLength || ''}
                            onChange={handleQuestionChange}
                        />
                        <input
                            type="text"
                            name="mandatory"
                            placeholder="Mandatory Question?"
                            value={currentQuestion.mandatory || ''}
                            onChange={handleQuestionChange}
                        />
                    </div>
                    <div className="input-group">
                        {/* <input
                        type="text"
                        name="options"
                        placeholder="Has Options to Select?"
                        value={currentQuestion.options || ''}
                        onChange={handleQuestionChange}
                    /> */}
                        <input
                            type="text"
                            name="callValidation"
                            placeholder="Validation to call"
                            ref={callValidationInputRef}
                        />
                        <button className="add-button" onClick={handleValidationSubmit}>Add Validation</button>
                    </div>
                    <div className="input-group">
                        <input
                            type="text"
                            name="optionValue"
                            placeholder="Option Value"
                            value={currentQuestion.optionValue || ''}
                            onChange={(e) =>
                                handleOptionChange(0, 'value', e.target.value)
                            }
                        />
                        <input
                            type="text"
                            name="optionLabel"
                            placeholder="Option Label"
                            value={currentQuestion.optionLabel || ''}
                            onChange={(e) =>
                                handleOptionChange(0, 'label', e.target.value)
                            }
                        />
                        <button onClick={addOption}>Add Option</button>
                    </div>
                    <div className="input-group">
                        <button onClick={addQuestion}>Add Question</button>
                    </div>
                </div>

                <table className="questions-table">
                    <thead>
                        <tr>
                            <th className="small-column">ID</th>
                            <th className="large-column">Text</th>
                            <th className="small-column">Field</th>
                            <th className="small-column">Field Length</th>
                            <th className="small-column">Mandatory</th>
                            <th className="large-column">Options</th>
                            <th className="medium-column">Call Validations</th>
                            <th className="small-column">Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {questions.map((question, index) => (
                            <tr
                                key={index}
                                draggable={true}
                                onDragStart={() => handleDragStart(index)}
                                onDragOver={() => handleDragOver(index)}
                                onDragEnd={handleDragEnd}
                            >
                                <td>{question.id}</td>
                                <td>{question.text}</td>
                                <td>{question.field}</td>
                                <td>{question.fieldLength}</td>
                                <td>{question.mandatory}</td>
                                <td>
                                    {question.options.map((option, optionIndex) => (
                                        <div key={optionIndex}>
                                            Value: {option.value} - Label: {option.label}
                                        </div>
                                    ))}
                                </td>
                                <td>
                                    {question.callValidations.map((validation, validationIndex) => (
                                        <div key={validationIndex}>
                                            <input
                                                type="text"
                                                value={validation}
                                                onChange={(e) =>
                                                    handleValidationChange(validationIndex, e.target.value)
                                                }
                                            />
                                        </div>
                                    ))}
                                </td>
                                <td>
                                    {editingIndex === index ? (
                                        <div>
                                            <button onClick={saveEditedQuestion}>Save</button>
                                        </div>
                                    ) : (
                                        <div>
                                            <button onClick={() => startEditing(index)}>Edit</button>
                                            <button onClick={() => deleteQuestion(index)}>Delete</button>
                                        </div>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="input-group">
                    <button onClick={addCallValidation}>Add Validation</button>
                </div>

            </div>
        </div>
    );
};

const goBack = () => {
    // Handle navigation to the previous page
};

export default AddChatbotFields;
