// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';

import UploadForm from './UploadForm';

function App() {
  const [processedImage, setProcessedImage] = useState(null);

  const handleImageUpload = async (imageData) => {
    try {
      const response = await axios.post('http://localhost:5000/processImage', {
        imageData,
      });

      setProcessedImage(response.data.imagePath);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Image Processing</h1>
      <UploadForm onImageUpload={handleImageUpload} />
      {processedImage && (
        <div>
          <h2>Processed Image</h2>
          <img src={`http://localhost:5000/${processedImage}`} alt="Processed" />
        </div>
      )}
    </div>
  );
}

export default Photo;
