import React, { useState } from 'react';
import axios from 'axios';

import UploadForm from './UploadForm';

function Photo() {
  const [processedImage, setProcessedImage] = useState(null);

  const handleImageUpload = async (imageData) => {
    try {
      const response = await axios.post('http://localhost:5000/processImage', {
        imageData,
      });
      // Convert binary image data to a Blob
      const blob = new Blob([new Uint8Array(response.data)], { type: 'image/jpeg' });

      // Create a FileReader to read the Blob and convert to Data URL
      const reader = new FileReader();
      reader.onload = () => {
        setProcessedImage(reader.result);
      };
      reader.readAsDataURL(blob);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Image Processing</h1>
      <UploadForm onImageUpload={handleImageUpload} />
      {processedImage && (
        <div>
          <h2>Processed Image</h2>
          <img src={`http://localhost:5000/${processedImage}`} alt="Processed" />
        </div>
      )}
    </div>
  );
}

export default Photo;
