import React, { useState } from 'react';
import axios from 'axios';

import UploadForm from './UploadForm';

function Photo() {
  const [processedImage, setProcessedImage] = useState('./images/pew.jpeg');

  const handleImageUpload = async (imageData) => {
    try {
      const response = await axios.post('http://localhost:5000/processImage', {
        imageData,
      });
      // Convert binary image data to a Blob with the correct MIME type
      const blob = new Blob([new Uint8Array(response.data)], { type: 'image/jpeg' });

      // Create a Data URL from the Blob
      const dataUrl = URL.createObjectURL(blob);

      // Set the Data URL as the processed image
      setProcessedImage(dataUrl);
      console.log(processedImage);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Image Processing</h1>
      <UploadForm onImageUpload={handleImageUpload} />
      {processedImage && (
        <div>
          <h2>Processed Image</h2>
          <img src={processedImage} alt="Processed" />
        </div>
      )}
    </div>
  );
}

export default Photo;
