import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateFormEntry = () => {
  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [frontImage, setFrontImage] = useState(null);
  const [images, setImages] = useState([]);
  const navigate = useNavigate();

  const handleFrontImageChange = (e) => {
    const file = e.target.files[0];
    setFrontImage(file);
  };

  const handleImagesChange = (e) => {
    const selectedImages = e.target.files;
    setImages([...images, ...selectedImages]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('id', id);
    formData.append('name', name);
    formData.append('description', description);
    formData.append('frontImage', frontImage);

    for (let i = 0; i < images.length; i++) {
      formData.append('images', images[i]);
    }

    try {
      // Send the form data to the server
      await axios.post('http://localhost:3001/makeFormEntry', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // After successful submission, navigate to /createNewTemplate
      navigate('/createNewTemplate');
    } catch (error) {
      console.error('Error submitting form entry:', error);
    }
  };

  return (
    <div>
      <h2>Create Form Entry</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID:</label>
          <input type="text" value={id} onChange={(e) => setId(e.target.value)} required />
        </div>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </div>
        <div>
          <label>Front Image:</label>
          <input type="file" accept="image/*" onChange={handleFrontImageChange} required />
        </div>
        <div>
          <label>Images:</label>
          <input type="file" accept="image/*" multiple onChange={handleImagesChange} required />
        </div>
        <div>
          <button type="submit">Submit Form Entry</button>
        </div>
      </form>
    </div>
  );
};

export default CreateFormEntry;
