import React, { useState, useEffect, useRef } from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Select from '@material-ui/core/Select';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Icon from '@material-ui/core/Icon';
import CircularProgress from "@material-ui/core/CircularProgress";
import axios from './axios-object';
import withStyles from '@material-ui/core/styles/withStyles';

const styles = theme => ({
    main: {
        width: 'auto',
        display: 'block', // Fix IE 11 issue.
        marginLeft: theme.spacing.unit * 3,
        marginRight: theme.spacing.unit * 3,
        [theme.breakpoints.up(400 + theme.spacing.unit * 3 * 2)]: {
            width: 400,
            marginLeft: 'auto',
            marginRight: 'auto',
        },
    },
    paper: {
        marginTop: theme.spacing.unit * 8,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
    },
    avatar: {
        margin: theme.spacing.unit,
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing.unit,
    },
    submit: {
        marginTop: theme.spacing.unit * 3,
    },
    input: {
        display: 'none',
    },
    button: {
        margin: theme.spacing.unit,
    },
    rightIcon: {
        marginLeft: theme.spacing.unit,
    },
    progress: {
        margin: theme.spacing.unit * 2,
    }
});

const GetData = (props) => {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [tid, setTid] = useState(null);
    const [image, setImage] = useState(null);
    const [labelWidth, setLabelWidth] = useState(0);

    const InputLabelRef = useRef(null);

    useEffect(() => {
        setLabelWidth(InputLabelRef.current.offsetWidth);

        axios.post('/prevTemplates/', JSON.stringify(localStorage.getItem("username")))
            .then(response => setData(response.data));
    }, []);

    const handleChange = (event) => {
        setTid(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        if (tid === null) {
            setError("Select A Valid Template");
        } else {
            setSubmitted(true);
            let formData = new FormData();
            formData.append('image', image);
            axios.post('/submitform/' + tid, formData)
                .then(response => {
                    alert("Template Submitted Successfully");
                    props.history.push("/viewdata/" + tid + "/" + response.data.id);
                })
                .catch(_ => alert("Template Submission Failed"));
        }
    }

    const loadFile = (event) => {
        var imageElement = document.getElementById('image');
        imageElement.src = URL.createObjectURL(event.target.files[0]);
        setImage(event.target.files[0]);
    }

    const { classes } = props;

    return (
        <main className={classes.main}>
            <CssBaseline>
                <Paper className={classes.paper}>
                    <Typography component="h1" variant="h5">
                        Get Data
                    </Typography>
                    <br />
                    <form onSubmit={handleSubmit} className={classes.form}>
                        <div className="form-group">
                            <FormControl variant="outlined" className={classes.formControl}>
                                <InputLabel
                                    ref={InputLabelRef}
                                    htmlFor="outlined-age-native-simple"
                                >
                                    Template
                                </InputLabel>
                                <Select
                                    native
                                    value={tid}
                                    onChange={handleChange}
                                    input={
                                        <OutlinedInput
                                            name="template"
                                            labelWidth={labelWidth}
                                            id="outlined-age-native-simple"
                                        />
                                    }
                                >
                                    <option value="" />
                                    {data.map((item, key) => {
                                        return (
                                            <option key={key} value={item.id}>{item.name}</option>
                                        );
                                    })}
                                </Select>
                            </FormControl>
                        </div>
                        <input
                            accept="image/*"
                            className={classes.input}
                            style={{ display: 'none' }}
                            id="formimage"
                            multiple
                            type="file"
                            onChange={loadFile}
                        />
                        <label htmlFor="formimage">
                            <Button variant="contained" color="default" component="span" className={classes.button}>
                                Upload Form
                                <CloudUploadIcon className={classes.rightIcon} />
                            </Button>
                        </label>
                        <img className="img-fluid" id="image" alt="" />

                        {image !== null && !submitted && (
                            <Button variant="contained" color="primary" className={classes.button} type="submit">
                                Send
                                <Icon className={classes.rightIcon}>send</Icon>
                            </Button>
                        )}
                        {submitted && (
                            <div>
                                <CircularProgress className={classes.progress} />
                            </div>
                        )}
                    </form>
                </Paper>
            </CssBaseline>
        </main>
    );
}

GetData.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(GetData);
