import React, { Component } from 'react';

class ImageDisplay extends Component {
  state = {
    imageUrl: '',
  };

  componentDidMount() {
    // Make a request to fetch the image from the server
    fetch('/getImage')
      .then((response) => response.blob())
      .then((blob) => {
        // Check if the Blob has a valid type (e.g., image/jpeg, image/png)
        if (blob.type.startsWith('image/')) {
          // Create a Blob URL for the image
          const imageUrl = URL.createObjectURL(blob);
          this.setState({ imageUrl });
        } else {
          console.error('Invalid Blob type:', blob.type);
          // Handle the case where the Blob is not a valid image
        }
      })
      .catch((error) => {
        console.error('Error fetching image:', error);
        // Handle the fetch error here
      });
  }

  render() {
    return (
      <div>
        {this.state.imageUrl && (
          <img src={this.state.imageUrl} alt="Server Image" />
        )}
      </div>
    );
  }
}

export default ImageDisplay;
