import React, { useState, useRef } from 'react';

const AddChatbotFields = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState({ id: 1 });
    const [optionsText, setOptionsText] = useState('');
    const [isAddingOptions, setIsAddingOptions] = useState(false);
    const [callValidation, setCallValidation] = useState('');

    const callValidationInputRef = useRef(null);

    let qid = 2;

    const handleQuestionChange = (e) => {
        const { name, value } = e.target;
        setCurrentQuestion({
            ...currentQuestion,
            [name]: value,
        });

    };

    // const handleValidationChange = (e) => {
    //     const { value } = e.target;

    // };

    const handleValidationSubmit = () => {
        if (callValidationInputRef.current.value) {
            if (!currentQuestion.callValidations) {
                currentQuestion.callValidations = [];
                
            } else {
                
            }
            currentQuestion.callValidations.append(callValidationInputRef.current.value);
        }
        // const { name, value } = e.target;
        // setCurrentQuestion({
        //     ...currentQuestion,
        //     [name]: value,
        // });

    };

    const addQuestion1 = () => {
        if (currentQuestion.text && currentQuestion.field && currentQuestion.mandatory && currentQuestion.callValidations) {
            if (currentQuestion.options === true) {
                setIsAddingOptions(true);
            } else {
                setQuestions([...questions, currentQuestion]);
                setCurrentQuestion({ id: qid });
            }
            qid += 1;
        }
        console.log(questions)
    };

    const addOption = () => {
        if (optionsText) {
            const updatedQuestions = [...questions];
            updatedQuestions[questions.length - 1].options.push(optionsText);
            setQuestions(updatedQuestions);
            setOptionsText('');
        }
    };

    return (
        <div>
            <h2>Add Chatbot Fields</h2>
            {isAddingOptions ? (
                <div>
                    <input
                        type="text"
                        name='optionValue'
                        placeholder="Option Text"
                        value={optionsText}
                        onChange={(e) => setOptionsText(e.target.value)}
                    />
                    <button onClick={addOption}>Add Option</button>
                </div>
            ) : (
                <div>
                    <input
                        type="text"
                        name="text"
                        placeholder="Question Text"
                        value={currentQuestion.text || ''}
                        onChange={handleQuestionChange}
                    />
                    <input
                        type="text"
                        name="field"
                        placeholder="Field"
                        value={currentQuestion.field || ''}
                        onChange={handleQuestionChange}
                    />
                    <input
                        type="text"
                        name="fieldLength"
                        placeholder="Field Length"
                        value={currentQuestion.fieldLength || ''}
                        onChange={handleQuestionChange}
                    />
                    <input
                        type="text"
                        name="mandatory"
                        placeholder="Mandatory Question?"
                        value={currentQuestion.mandatory || ''}
                        onChange={handleQuestionChange}
                    />
                    <input
                        type="text"
                        name="options"
                        placeholder="Has Options to Select?"
                        value={currentQuestion.options || ''}
                        onChange={handleQuestionChange}
                    />
                    <input
                        type="text"
                        name="callValidation"
                        placeholder="Validation to call"
                        ref={callValidationInputRef}
                    />
                    <button onClick={handleValidationSubmit}>Add Validation</button>
                    <input
                        type="text"
                        name='optionValue'
                        placeholder="Option Text"
                        value={optionsText}
                        onChange={(e) => setOptionsText(e.target.value)}
                    />
                    <button onClick={addOption}>Add Option</button>
                    <input
                        type="text"
                        name="last"
                        placeholder="Last Question?"
                        value={currentQuestion.last || ''}
                        onChange={handleQuestionChange}
                    />
                    <button onClick={addQuestion1}>Add Question</button>
                </div>
            )}
            <ul>
                {questions.map((question, index) => (
                    <li key={index}>
                        {question.text} - {question.field} - {question.mandatory} - {question.callValidations}
                        {question.options && question.options.length > 0 && (
                            <ul>
                                {question.options.map((option, optionIndex) => (
                                    <li key={optionIndex}>{option}</li>
                                ))}
                            </ul>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AddChatbotFields;
