import React, { Component } from 'react';

class ImageUploader extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedImage: null,
    };
  }

  handleImageChange = (e) => {
    const file = e.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        this.setState({
          selectedImage: e.target.result,
        });
      };

      reader.readAsDataURL(file);
    }
  };

  render() {
    return (
      <div>
        {this.state.selectedImage ? (
          <img src={this.state.selectedImage} alt="Selected" width="200" height="200" />
        ) : (
          <div>
            <input
              type="file"
              accept="image/*"
              onChange={this.handleImageChange}
            />
          </div>
        )}
      </div>
    );
  }
}

export default Mark;
