import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import MailIcon from '@mui/icons-material/Mail';

const drawerWidth = 240;

const PersistentDrawerLeft = (props) => {
  const [open, setOpen] = React.useState(false);

  const handleClick = (link) => {
    props.history.push(link);
  };

  const { options, children } = props;

  return (
    <div className="root">
      <CssBaseline />
      <AppBar
        position="fixed"
        sx={{
          transition: 'margin',
          '&.appBarShift': {
            width: `calc(100% - ${drawerWidth}px)`,
            marginLeft: drawerWidth,
            transition: 'margin',
          },
        }}
      >
        <Toolbar disableGutters={!open}>
          <IconButton
            color="inherit"
            aria-label="Open drawer"
            onClick={() => setOpen(true)}
            className={classNames('menuButton', open && 'hide')}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" color="inherit" noWrap>
            Document Scanner
          </Typography>
        </Toolbar>
      </AppBar>

      <Drawer
        className="drawer"
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: 'drawerPaper',
        }}
      >
        <div className="drawerHeader">
          <IconButton onClick={() => setOpen(false)}>
            {props.theme.direction === 'ltr' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </div>
        <Divider />
        <List>
          {Object.keys(options).map((text, index) => (
            <ListItem button key={index} onClick={() => handleClick(options[text])}>
              <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      <main className={classNames('content', { 'contentShift': open })}>
        <div className="drawerHeader" />
        {children}
      </main>
    </div>
  );
};

PersistentDrawerLeft.propTypes = {
  options: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired,
};

export default PersistentDrawerLeft;
