// frontend/src/UploadForm.js
import React, { useState } from 'react';

function UploadForm({ onImageUpload }) {
  const [imageData, setImageData] = useState('');

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setImageData(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   onImageUpload(imageData);
  // };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Send the image data to the server
    try {
      const response = await fetch('http://localhost:5000/processImage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ imageData }), // Send the base64-encoded image data
      });

      const result = await response.json();

      // Display the processed image
      // You can use the result.imagePath to display the image on the frontend
    } catch (error) {
      console.error('Error processing image:', error);
    }
  };

  return (
    <div>
      <h2>Upload Image</h2>
      <form onSubmit={handleSubmit}>
        <input type="file" accept=".jpg,.jpeg,.png" onChange={handleImageChange} />
        <button type="submit">Process Image</button>
      </form>
    </div>
  );
}

export default UploadForm;
