import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import withStyles from '@material-ui/core/styles/withStyles';
import Select from '@material-ui/core/Select';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import Icon from '@material-ui/core/Icon';
import CircularProgress from "@material-ui/core/CircularProgress";
import axios from './axios-object';
import { setTemplateId , setDId, getTemplateId } from './values';

const styles = theme => ({
    main: {
      width: 'auto',
      display: 'block', // Fix IE 11 issue.
      marginLeft: theme.spacing.unit * 3,
      marginRight: theme.spacing.unit * 3,
      [theme.breakpoints.up(400 + theme.spacing.unit * 3 * 2)]: {
        width: 400,
        marginLeft: 'auto',
        marginRight: 'auto',
      },
    },
    paper: {
      marginTop: theme.spacing.unit * 8,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: `${theme.spacing.unit * 2}px ${theme.spacing.unit * 3}px ${theme.spacing.unit * 3}px`,
    },
    avatar: {
      margin: theme.spacing.unit,
      backgroundColor: theme.palette.secondary.main,
    },
    form: {
      width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing.unit,
    },
    submit: {
      marginTop: theme.spacing.unit * 3,
    },
    root: {
      display: 'flex',
      flexWrap: 'wrap',
    },
    formControl: {
      margin: theme.spacing.unit,
      minWidth: 300,
    },
    selectEmpty: {
      marginTop: theme.spacing.unit * 2,
    },
    button: {
      margin: theme.spacing.unit,
    },
    leftIcon: {
      marginRight: theme.spacing.unit,
    },
    rightIcon: {
      marginLeft: theme.spacing.unit,
    },
    iconSmall: {
      fontSize: 20,
    },
    progress: {
      margin: theme.spacing.unit * 2
    }
});

class GetData extends Component {
    constructor() {
        super();
        this.state = {
                data : [],
                error : null,
                submitted : false,
                tid : null,
                image : null,
                labelWidth: 0,
        }
    }

    static contextTypes = {
        router : PropTypes.object,
    }

    componentDidMount() {
        this.setState({
            labelWidth: ReactDOM.findDOMNode(this.InputLabelRef).offsetWidth,
          });
        axios.post('/prevTemplates/', JSON.stringify(localStorage.getItem("username")))
        .then(response => this.setState({data : response.data}));

    }

    handleChange = (event) =>{
        this.state.tid = event.target.value;
        setTemplateId(this.state.tid)
    }
    // handleSubmit = (event) =>{
    //     event.preventDefault();
    //     (this.state.tid === null)?this.setState({error: "Select A Valid Template"}):this.setState({submitted: true});
    //     let image = new FormData();
    //     image.append('image',this.state.image);
    //     axios.post('/submitform/'+this.state.tid,image).then((response) => {
    //       alert("Template Submitted Successfully");
    //       // this.context.router.history.push("/viewdata/"+this.state.tid+"/"+response.data.id);
    //       setTemplateId(this.state.tid);
    //       setDId(response.data.id);
    //       console.log('viewing')
    //       window.history.pushState({}, null, "/viewdata");
    //       window.dispatchEvent(new Event('popstate'));
    //     }).catch(_ => alert("Template Submission Failed"))
    // }

    handleSubmit = async (event) => {
      event.preventDefault();
      if (this.state.tid === null) {
        this.setState({ error: "Select A Valid Template" });
      } else {
        try {
          let image = new FormData();
          image.append('image', this.state.image);
          const response = await axios.post('/submitform/' + this.state.tid, image);
          alert("Template Submitted Successfully");
          this.setState({ submitted: true });
          setTemplateId(this.state.tid);
          setDId(response.data.id);
          console.log('viewing');
          window.history.pushState({}, null, "/viewdata");
          window.dispatchEvent(new Event('popstate'));
        } catch (error) {
          alert("Template Submission Failed");
          console.error(error);
        }
        
      }
    }
    
    

    // async handleSubmit(event) {
    //   event.preventDefault();
    //   if (getTemplateId() === null) {
    //     this.setState({ error: "Select A Valid Template" });
    //   } else {
    //     // console.log(this.state)
    //     this.setState({ submitted: true });
    
    //     let image = new FormData();
    //     image.append('image', this.state.image);
    //     console.log(this.state.tid)
    //     try {
    //       const response = await axios.post('/submitform/' + getTemplateId(), image);
    //       alert("Template Submitted Successfully");
          
    //       console.log('viewing');
    
    //       // Use setTimeout to introduce a 5-second delay
    //       await new Promise(resolve => setTimeout(resolve, 5000));

    //       setTemplateId(getTemplateId());
    //       setDId(response.data.id);
    
    //       window.history.pushState({}, null, "/viewdata");
    //       window.dispatchEvent(new Event('popstate'));
    //     } catch (error) {
    //       alert("Template Submission Failed");
    //     }
    //   }
    // }    

    loadFile = (event) => {
        var image = document.getElementById('image');
        image.src = URL.createObjectURL(event.target.files[0]);
        this.setState({image:event.target.files[0]});
        console.log(this.state)
      }

    render () {
        const { classes } = this.props;  
        return (
        <main className={classes.main}>
            <CssBaseline>
                <Paper className={classes.paper}>
                <Typography component="h1" variant = "h5">
                Get Data
                </Typography>
                <br/>
                <form onSubmit={this.handleSubmit} className={classes.form}>
                    <div className="form-group">
                        <FormControl variant="outlined" className={classes.formControl}>
                          <InputLabel
                            ref={ref => {
                              this.InputLabelRef = ref;
                            }}
                            htmlFor="outlined-age-native-simple"
                          >
                            Template
                          </InputLabel>

                          <Select
                            native
                            value={this.state.name}
                            onChange={this.handleChange}
                            input={
                              <OutlinedInput
                                name="template"
                                labelWidth={this.state.labelWidth}
                                id="outlined-age-native-simple"
                              />
                            }
                          >
                            <option value="" />
                            {this.state.data.map((item,key)=>{
                            return(
                            <option value ={item.id} > {item.name} </option>
                          )})}
                          </Select>
                        </FormControl>
                    </div>

                    <input
                      accept="image/*"
                      className={classes.input}
                      style={{ display: 'none' }}
                      id="formimage"
                      multiple
                      type="file"
                      onChange = {this.loadFile}
                    />
                    <label htmlFor="formimage">
                    <Button variant="contained" color="default" component="span" className={classes.button}>
                      Upload Form
                      <CloudUploadIcon className={classes.rightIcon} />
                    </Button>
                    </label>

                    <img className = "img-fluid" id = "image" alt = "" />

                    {this.state.image!=null && this.state.submitted==false &&
                    <Button variant="contained" color="primary" className={classes.button} type="submit">
                      Send
                      <Icon className={classes.rightIcon}>send</Icon>
                    </Button>}
                    {this.state.submitted &&
                    <div>
                      <CircularProgress className={classes.progress} />
                    </div>
                    }
                </form>
                </Paper>
            </CssBaseline>
        </main>
        )
    }
}

GetData.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  
  export default withStyles(styles)(GetData);
