import React, { useState , useEffect } from 'react';
import axios from 'axios';
import { saveAs } from 'file-saver';
import UploadForm from './UploadForm';

function Photo() {
  const [processedImage, setProcessedImage] = useState(null);

  const handleImageUpload = async (imageData) => {
    try {
      const response = await axios.post('http://localhost:5000/processImage', {
        imageData,
      });
      const blob = await response.blob(); // Get image data as Blob
      setpr(URL.createObjectURL(blob)); // Convert Blob to URL
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    console.log('Processed image changed:', processedImage);
  }, [processedImage]);

  return (
    <div>
      <h1>Image Processing</h1>
      <UploadForm onImageUpload={handleImageUpload} />
      {processedImage && (
        <div>
          <h2>Processed Image</h2>
          <img src={processedImage} alt="Processed" />
        </div>
      )}
    </div>
  );
}

export default Photo;
