import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './MarkTemp.css';
import { } from './Move';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import Button from '@material-ui/core/Button';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import axios from './axios-object';

const MarkTemp = ({ classes }) => {
  const [idCount, setIdCount] = useState(0);
  const [data, setData] = useState({});
  const [image, setImage] = useState(null);

  const addName = (event) => {
    const templateName = event.target.value;
    const updatedData = { ...data };
    Object.keys(updatedData).forEach((item) => {
      updatedData[item].templateName = templateName;
      updatedData[item].username = localStorage.getItem('username');
    });
    setData(updatedData);
  };

  const addDesc = (event) => {
    const templateDesc = event.target.value;
    const updatedData = { ...data };
    Object.keys(updatedData).forEach((item) => {
      updatedData[item].templateDesc = templateDesc;
    });
    setData(updatedData);
  };

  const submitFormHandler = () => {
    const keysList = Object.keys(data);
    if (keysList.length === 0 || data[keysList[0]].templateName === null) return;

    const imageFormData = new FormData();
    imageFormData.append('image', image);

    axios
      .post('/submit/', JSON.stringify(data))
      .then((response) => {
        const responseData = response.data;
        axios
          .post(`/submitimage/${responseData.tid}`, imageFormData)
          .then(() => {
            alert('Template Submitted Successfully');
            window.location.reload();
          })
          .catch(() => alert('Template Submission Failed'));
      })
      .catch(() => alert('Template Submission Failed'));
  };

  const loadFile = (event) => {
    const imageElement = document.getElementById('image');
    imageElement.src = URL.createObjectURL(event.target.files[0]);
    const linkElement = document.getElementById('link');
    linkElement.parentNode.removeChild(linkElement);
    setImage(event.target.files[0]);
  };

  const submitHandler = (id, fieldname, fielddesc, boxCount, span) => {
    if (fieldname === '') return;

    const translateLeft = span.getAttribute('data-x') || 0;
    const translateDown = span.getAttribute('data-y') || 0;

    const fieldDetails = {
      fieldname,
      desc: fielddesc,
      boxcount: parseInt(boxCount, 10),
      type: `sel1${id}`,
      left_x: span.offsetLeft - span.offsetLeft + translateLeft,
      right_x: span.offsetLeft + span.offsetWidth - span.offsetLeft + translateLeft,
      top_y: span.offsetTop - span.offsetTop + translateDown,
      bottom_y: span.offsetTop + span.offsetHeight - span.offsetTop + translateDown,
      image_width: span.offsetWidth,
      image_height: span.offsetHeight,
    };

    const updatedData = { ...data };
    updatedData[id] = fieldDetails;
    setData(updatedData);

    const templateSubmit = document.getElementById('templateSubmit');
    templateSubmit.style.display = 'block';

    const fname = document.getElementById(`${id} fname`);
    fname.style.display = 'none';
  };

  const addRectangle = (event) => {
    const fixedId = idCount;

    const span = document.createElement('span');
    span.className = 'resize-drag border2';
    span.id = `${idCount} box`;
    span.style.top = `${event.pageY - 3}px`;
    span.style.left = `${event.pageX - 3}px`;
    document.getElementById('form').appendChild(span);

    const accept = document.createElement('button');
    accept.className = 'accept';
    accept.id = idCount;
    accept.addEventListener('click', () => {
      fixSpan(accept.id);
    }, false);
    document.getElementById(span.id).appendChild(accept);

    const reject = document.createElement('button');
    reject.className = 'reject';
    reject.addEventListener('click', () => {
      removeRectangle(accept.id);
    }, false);
    document.getElementById(span.id).appendChild(reject);

    const fname = document.createElement('div');
    fname.className = 'fieldName';
    fname.id = `${idCount} fname`;
    fname.style.display = 'none';

    const finput = document.createElement('input');
    finput.type = 'text';
    finput.setAttribute('placeholder', 'Field Name');

    const fdesc = document.createElement('input');
    fdesc.type = 'text';
    fdesc.setAttribute('placeholder', 'Field Description');

    const type = document.createElement('div');
    const sel = document.createElement('select');
    const opt1 = document.createElement('option');
    const opt2 = document.createElement('option');
    const opt3 = document.createElement('option');

    type.className = 'form-group';
    sel.className = 'form-control';
    sel.id = `sel1${idCount}`;
    opt1.innerHTML = 'Text';
    opt2.innerHTML = 'Checkbox';
    opt3.innerHTML = 'Signature';

    sel.appendChild(opt1);
    sel.appendChild(opt2);
    sel.appendChild(opt3);
    type.appendChild(sel);

    const boxCount = document.createElement('input');
    boxCount.type = 'number';
    boxCount.setAttribute('value', '1');

    const fsubmit = document.createElement('input');
    fsubmit.addEventListener('click', () => {
      submitHandler(fixedId, finput.value, fdesc.value, boxCount.value, span);
    }, false);
    fsubmit.type = 'submit';
    fsubmit.value = 'Submit';

    fname.appendChild(finput);
    fname.appendChild(fdesc);
    fname.appendChild(type);
    fname.appendChild(boxCount);
    fname.appendChild(fsubmit);
    span.appendChild(fname);

    setIdCount(idCount + 1);
  };

  const fixSpan = (Boxid) => {
    const span = document.getElementById(`${Boxid} box`);
    span.style.top = `${span.offsetTop}px`;
    span.style.left = `${span.offsetLeft}px`;
    span.className = 'border2';

    const accept = document.getElementById(Boxid);
    accept.style.backgroundColor = 'Orange';
    accept.addEventListener('click', () => {
      resizeAgain(accept.id);
    }, false);

    const fname = document.getElementById(`${Boxid} fname`);
    fname.style.display = 'block';
  };

  const resizeAgain = (Boxid) => {
    const span = document.getElementById(`${Boxid} box`);
    span.className = 'resize-drag border2';

    const fname = document.getElementById(`${Boxid} fname`);
    fname.style.display = 'none';

    const accept = document.getElementById(Boxid);
    accept.style.backgroundColor = 'Greenyellow';
    accept.addEventListener('click', () => {
      fixSpan(accept.id);
    }, false);
  };

  const removeRectangle = (Boxid) => {
    const span = document.getElementById(`${Boxid} box`);
    const id = parseInt(span.id[0], 10);
    const updatedData = { ...data };
    delete updatedData[id];
    setData(updatedData);
    span.parentNode.removeChild(span);
  };

  return (
    <div className="wrapper" id="wrap">
      <CssBaseline />

      <div id="imagePanel">
        <input
          accept="image/*"
          className={classes.input}
          style={{ display: 'none' }}
          id="file"
          multiple
          type="file"
          onChange={loadFile}
        />
        <label htmlFor="file" id="link">
          <Button variant="contained" color="default" component="span" className={classes.button}>
            Upload Form
            <CloudUploadIcon className={classes.rightIcon} />
          </Button>
        </label>

        <div className="template" id="form">
          <img className="img-fluid" id="image" onClick={addRectangle} alt="" />
        </div>
      </div>

      <div id="layerPanel">
        <Typography variant="display1">Fields</Typography>
        <ul id="templateFields">
          {Object.keys(data).map((key, index) => (
            <div key={key}>
              <li>{data[key].fieldname}</li>
              <ul>
                <li>Type   : {data[key].type}</li>
                <li>Description : {data[key].desc}</li>
                <li>Box Count : {data[key].boxcount}</li>
                <li>Left   : {data[key].left_x}</li>
                <li>Right  : {data[key].right_x}</li>
                <li>Top    : {data[key].top_y}</li>
                <li>Bottom : {data[key].bottom_y}</li>
              </ul>
            </div>
          ))}
        </ul>
        <div id="templateSubmit">
          <input type="text" onChange={addName} placeholder="Template Name" />
          <input type="text" onChange={addDesc} placeholder="Template Description" />
          <input type="submit" value="Submit Template" onClick={submitFormHandler} />
        </div>
      </div>
    </div>
  );
};

MarkTemp.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default MarkTempNew;
