import React, { useState } from 'react';

const FormsList = () => {
  const [selectedForm, setSelectedForm] = useState(null);

  const forms = [
    {
      name: 'Form 1',
      description: 'Description for Form 1',
      frontImage: require('../images/pew.jpeg'), // Use require for local images
      images: ['url_to_image_1', 'url_to_image_2', 'url_to_image_3'],
    },
    {
      name: 'Form 2',
      description: 'Description for Form 2',
      frontImage: require('./images/front_image_2.jpg'), // Use require for local images
      images: ['url_to_image_4', 'url_to_image_5'],
    },
    // Add more forms as needed
  ];

  const handleFormClick = (form) => {
    setSelectedForm(form);
  };

  const handleBackButtonClick = () => {
    setSelectedForm(null);
  };

  return (
    <div className="forms-list">
      {selectedForm ? (
        <div className="selected-form">
          <h2>{selectedForm.name}</h2>
          <p>{selectedForm.description}</p>
          <button onClick={handleBackButtonClick}>Back to Forms List</button>
          <div className="form-images">
            {selectedForm.images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`Page ${index + 1}`}
                className="form-image"
              />
            ))}
          </div>
        </div>
      ) : (
        <ul className="forms">
          {forms.map((form, index) => (
            <li
              key={index}
              className="form"
              onClick={() => handleFormClick(form)}
            >
              <div className="form-preview">
                <img src={form.frontImage} alt={`${form.name} Preview`} />
              </div>
              <div className="form-details">
                <h3>{form.name}</h3>
                <p>{form.description}</p>
                <button onClick={() => handleFormClick(form)}>View Details</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default FormsList;
