import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import CssBaseline from '@material-ui/core/CssBaseline';
import FormControl from '@material-ui/core/FormControl';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import { Link, useHistory } from 'react-router-dom';
import axios from './axios-object';

const styles = theme => ({
  main: {
    width: 'auto',
    display: 'block',
    marginLeft: theme.spacing(3),
    marginRight: theme.spacing(3),
    [theme.breakpoints.up(400 + theme.spacing(6))]: {
      width: 400,
      marginLeft: 'auto',
      marginRight: 'auto',
    },
  },
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: `${theme.spacing(2)}px ${theme.spacing(3)}px ${theme.spacing(3)}px`,
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    marginTop: theme.spacing(3),
  },
});

const Login = ({ classes }) => {
  const history = useHistory();

  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });

  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);
  const loggedin = localStorage.getItem("username");

  const handleSubmit = event => {
    event.preventDefault();
    axios
      .post('/login', JSON.stringify(formData))
      .then(_ => {
        console.log("here");
        setSubmitted(true);
        localStorage.setItem("username", formData.username);
      })
      .catch(error => {
        if (error.response && error.response.data) {
          setError(error.response.data.error);
          setSubmitted(true);
        }
      });
  };

  const handleUChange = event => {
    setFormData({ ...formData, username: event.target.value });
  };

  const handlePChange = event => {
    setFormData({ ...formData, password: event.target.value });
  };

  return (
    <main className={classes.main}>
      <CssBaseline />
      <Typography variant='h5' align='center' gutterBottom>
        Welcome to Document Scanner
      </Typography>
      {loggedin == null && (
        <Paper className={classes.paper}>
          <Avatar className={classes.avatar}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <form className={classes.form} onSubmit={handleSubmit}>
            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="text">Username</InputLabel>
              <Input
                id="email"
                name="email"
                autoComplete="email"
                value={formData.username}
                onChange={handleUChange}
                autoFocus
              />
            </FormControl>

            <FormControl margin="normal" required fullWidth>
              <InputLabel htmlFor="password">Password</InputLabel>
              <Input
                name="password"
                type="password"
                id="password"
                autoComplete="current-password"
                value={formData.password}
                onChange={handlePChange}
              />
            </FormControl>
            <br />
            <br />
            <Typography>
              <Link to={'/signup'}>
                <PersonAddIcon /> New to Template Marker? Sign Up
              </Link>
            </Typography>
            <br />
            {submitted && (
              <div>
                <Typography>
                  User Logged In
                  {window.location.reload()}
                </Typography>
              </div>
            )}

            {!submitted && (
              <div className="text-danger">{error}</div>
            )}

            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              Sign in
            </Button>
          </form>
        </Paper>
      )}

      {loggedin != null && history.push("/getdata")}
    </main>
  );
};

Login.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Login);
